    // Create connection
    ...
  }

  protected override void Dispose( bool disposing )
  {
    base.Dispose( disposing );
  }

  protected override void OnClosing(System.ComponentModel.CancelEventArgs e)
  {
    if( dbMgr != null ) {
      dbMgr.SetServerSyncListener( null, null, null );
      dbMgr = null;
